def parse_brand_option(s):
    options = s.split("</option>")
    brand_id_map = dict()

    for op in options:
        if len(op) == 0:
            continue
        id_s = op.split(">")[0].split("<option ")[-1][7:-1]
        brand_name = op.split(">")[1]
        #print (id_s),
        #print (brand_name)
        brand_id_map[int(id_s)] = brand_name

    print ("Total brand options we have ", len(brand_id_map))
    sort_keys = sorted(brand_id_map.keys())

    #print (brand_id_map)

    for k in sort_keys:
        #print (k, brand_id_map[k])
        print (k)

